import React, { Component } from 'react';
import axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';


const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: 'white',
  },
  gridList: {
    width: 500,
    height: 450,
  },
  
  });
class Listado extends Component {

    state = {
        imagenes: [],
        
       

    }
    componentDidMount(){
        axios.get('http://172.16.0.64:8000/api/imagen').then(res=>{
            const imagenes = res.data;
            this.setState({imagenes});
        });
    }

    
    render() {
        const classes = styles();
        return (
            <div className={classes.root}>
              <GridList cellHeight={160} className={classes.gridList} cols={3}>
                {this.state.imagenes.map(imagen => (
                  <GridListTile key={imagen.id} cols={imagen.col || 1}>
                    <img src={'images/'+imagen.image} alt={imagen.name} />      
                  </GridListTile>
                ))}
              </GridList>
            </div>            
        );
    }
}

export default   withStyles(styles)(Listado);
