import React, { Component } from 'react';
import axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import Button from '@material-ui/core/Button';
import { Redirect } from 'react-router-dom';
const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  titulosTabla:
  {
      fontWeight: 'bold',
  },
  gridList: {
    width: 550,
    height: 550,
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: 'translateZ(0)',
  },
  titleBar: {
    background:
      'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, ' +
      'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
  },
  icon: {
    color: 'white',
  },
  });
class Listado extends Component {

    state = {
        productos: [],
        pro:[],
        toDashboard: false,
        
    }
    componentDidMount(){
        axios.get('http://localhost:51769/Api/Productos').then(res=>{
            const productos = res.data;
            this.setState({productos});
        });
    }

    handleClick =id => event =>
    {
        
            axios.get('http://localhost:51769/Api/Productos/'+id).then(res=>{
                
                const pro = res.data;
                this.setState({pro});
                this.setState({ toDashboard: true});
            });
       
    }
    render() {
      const {classes} = this.props;
      if (this.state.toDashboard === true) {
        return <Redirect to={{
            pathname: '/transicion',
            state: { data: this.state.pro}
        }} />
      }
        return (
          <Paper className={classes.root}>
          <Table className={classes.table}>
              <TableHead>
                  <TableRow>
                      <TableCell className={classes.titulosTabla} >DESCRIPCION</TableCell>
                      <TableCell className={classes.titulosTabla}>ID.PROVEEDOR</TableCell>
                      <TableCell className={classes.titulosTabla}>PRECIO UNITARIO</TableCell>
                      <TableCell className={classes.titulosTabla}> </TableCell>
                  </TableRow>
              </TableHead>
              <TableBody id = 'cuerpoTabla'>
                  {
                      this.state.productos.map(producto => {
                          return (
                            <TableRow key={producto.id}>
                                    <TableCell>
                                      {producto.ProductName}
                                    </TableCell>
                                    <TableCell>
                                      {producto.SupplierId}
                                    </TableCell>
                                    <TableCell>
                                      {producto.UnitPrice}
                                    </TableCell>
                                    <TableCell>
                                  <img src={"/images/"+producto.image} alt={producto.name} onClick={this.handleClick(producto.id)}/>
                                  </TableCell>
                              </TableRow>
                          );
                      })
                  }
              </TableBody>
          </Table>
      
      <Button variant="contained" color="primary" className={classes.button} onClick={this.handleSubmit}>
          Primary
      </Button>
      </Paper>
      
        );
    }
}
export default   withStyles(styles)(Listado);
