import React, { Component } from 'react';
import  {withStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import AppBar from '@material-ui/core/AppBar';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
const styles = theme => ({

   
    root: {
        width: '100%',
        marginTop: theme.spacing.unit * 3,
        overflowX: 'auto',
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
    },
    button: {
        margin: theme.spacing.unit,
        height:35,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',

    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
        marginTop: 0,
        marginBottom:0,
    },
    contenedor:
        {
            align: 'center',
        },

       
          titulosTabla:
          {
              fontWeight: 'bold',
              color: 'DodgerBlue',
          padding: '40px',
          fontFamily: 'Arial',
          textAlign: 'center',
          },
          cuerpoTabla:
          {
                  
          padding: '40px',
          fontFamily: 'Arial',
          textAlign: 'center',
          },
         
          table: {
            background:
              'linear-gradient(to top, rgba(0,0,0,0.7) 0%, ' +
              'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
            width:"80%",
          
              
          },
});
class Formulario extends Component {
    constructor(props){
        super(props);
    
        this.state = {
            habitaciones: [],
            descripcion:'',
            estado:'',
            precio:'',
            numCamas:'',
            
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangePanel = this.handleChangePanel.bind(this);
        
    }
    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
     };
    handleSubmit() { 
       
        const Habitacion =new FormData();
         
            Habitacion.set('descripcion',this.state.descripcion);
            Habitacion.set('estado',this.state.estado);
            Habitacion.set('precio',this.state.precio);
            Habitacion.set('numCamas',this.state.numCamas);
            
        
        console.log(Habitacion);
        axios.post('http://localhost:8080/hotel/', {Habitacion}).then(res => {
            console.log(res);
            console.log(res.data);
            axios.get('http://localhost:8080/hotel/habitaciones/').then(res=>{
            const habitaciones = res.data;
            this.setState({habitaciones});
            });
          });
        
        
    }
    handleChangePanel = panel => (event, expanded) => {
      
        this.setState({
          expanded: expanded ? panel : false,
        });
      };
    componentDidMount(){
        axios.get('http://localhost:8080/hotel/habitaciones/').then(res=>{
            const habitaciones = res.data;
            this.setState({habitaciones});
        });
    };
    render() {
        const { classes } = this.props;
       
        return (            
            <form className={classes.root}>
                <AppBar>INGRESE UNA NUEVA HABITACION</AppBar>
                <br/>
                <br/>
                <br/>
                <TextField
                    label="Descripcion"
                    className={classes.textField}
                    onChange={this.handleChange('descripcion')}
                />
                <TextField                           
                    label="Estado"
                    className={classes.textField}
                    onChange={this.handleChange('estado')}
                />
                <TextField
                    label="Precio"
                    type="number"
                    onChange={this.handleChange('precio')}
                    className={classes.textField}
                    
                    
                />
                <TextField
                     label="numCamas"
                     value={this.state.numCamas}
                     onChange={this.handleChange('numCamas')}
                     type="number"
                     className={classes.textField}
                     InputLabelProps={{
                       shrink: true,
                     }}
                     margin="normal"
                />
                <Button variant="contained" color="primary" className={classes.button} onClick={this.handleSubmit}>Guardar</Button>
                <br/>
                <br/>
                <br/>          
                <Paper className={classes.root}>
          <Table className={classes.table}>
              <TableHead>
                  <TableRow>
                      <TableCell className={classes.titulosTabla} >DESCRIPCION</TableCell>
                      <TableCell className={classes.titulosTabla}>NUM. CAMAS</TableCell>
                      <TableCell className={classes.titulosTabla}>PRECIO </TableCell>
                      <TableCell className={classes.titulosTabla}>ESTADO </TableCell>
                  </TableRow>
              </TableHead>
              <TableBody id = 'cuerpoTabla' >
                  {
                      this.state.habitaciones.map(habitacion => {
                          return (
                            <TableRow key={habitacion.idHabitacion} >
                                    <TableCell className={classes.cuerpoTabla}>
                                      {habitacion.descripcion}
                                    </TableCell>
                                    <TableCell className={classes.cuerpoTabla}>
                                      {habitacion.numCamas}
                                    </TableCell>
                                    <TableCell className={classes.cuerpoTabla}>
                                      {habitacion.precio}
                                    </TableCell>
                                    <TableCell className={classes.cuerpoTabla}>
                                       {habitacion.estado}
                                  </TableCell>
                              </TableRow>
                          );
                      })
                  }
              </TableBody>
          </Table>
      </Paper>
        </form>       

        );
    }
}
export default withStyles(styles)(Formulario);
