import React, { Component } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import {  Redirect } from 'react-router-dom';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
      },
      gridList: {
        width: 550,
        height: 'auto',
        justify:"center",
        alignItems:"center"
      },
     
    button: {
        margin:theme.spacing(2),
        height:30,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',

    },
    textField: {
        marginLeft:theme.spacing(2),
        marginRight:theme.spacing(2),
        width: 200,
        marginTop: 0,
        marginBottom:0,
    },
    contenedor:
        {
            align: 'center',
        },

   
});

class Imagen extends Component {
    constructor(props){
        super(props);
    
        this.state = {
            imagen: [],
            ancho:0,
            alto:0,
            toDashboard: false,
            
        }   
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        
    }
    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
        var box = document.getElementById('img2')
        if(name==='ancho')
            box.width=event.target.value ;
        else
            box.height=event.target.value;
     };

    componentDidMount(){
        if(this.props.location.state!=null){
            axios.get('http://localhost:8000/api/imagen/'+this.props.location.state.data.id).then(res=>{
                const imagen = res.data;
                this.setState({imagen});
                console.log(imagen)
            });
        }else{
            axios.get('http://localhost:8000/api/imagen/').then(res=>{
                const imagen = res.data;
                this.setState({imagen});
                this.setState({ toDashboard: true});              
            });
        }
    }
    handleSubmit() { 
        const medidas = {
            ancho:this.state.ancho,
            alto:this.state.alto,
        };
        
        axios.post('http://localhost:8000/api/imagen/'+this.props.location.state.data.id, {medidas}).then(res => {
            console.log(res);
            console.log(res.data);
            const respuesta = res.data;
            this.setState({respuesta});
            this.setState({ toDashboard: true});

        });
        
        
    }
    
      render() {
        const {classes} = this.props;
        if (this.state.toDashboard === true) {
            return <Redirect to={{
                pathname: '/',
                state: { data: this.state.respuesta}
            }} />
          }
        return (
                    <form className={classes.container} noValidate autoComplete="off">
                            <GridList  spacing={1} className={classes.gridList}>
                                <GridListTile key={this.state.imagen.id} style={{justifyContent: 'center'}}>
                                    <img src={this.state.imagen.image} alt={this.state.imagen.name} />
                                </GridListTile>
                            </GridList>
                            <GridList spacing={1} className={classes.gridList}>
                                <GridListTile key={this.state.imagen.id} >
                                <img src={this.state.imagen.image} alt={this.state.imagen.name}  id="img2"/>
                                </GridListTile>
                            </GridList>
                        <TextField
                            required
                            id="standard-required"
                            label="Alto"
                            className={classes.textField}
                            onChange={this.handleChange('alto')}
                            margin="normal"
                        />
                        <TextField
                            required
                            id="standard-required"
                            label="Ancho"
                            className={classes.textField}
                            onChange={this.handleChange('ancho')}
                            margin="normal"
                        />
                        <Button variant="contained" color="primary" className={classes.button} onClick={this.handleSubmit}>
                            Cambiar
                        </Button>
                    </form>
        );
    }
}
Imagen.propTypes = {
    classes: PropTypes.object.isRequired,
};
   export default withStyles(styles)(Imagen);;
