import React, { Component } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const styles = theme => ({

    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
      },
      gridList: {
        width: 550,
        height: 550,
        // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
        transform: 'translateZ(0)',
      },
    button: {
        margin: theme.spacing.unit,
        height:20,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',

    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
        marginTop: 0,
        marginBottom:0,
    },
    contenedor:
        {
            align: 'center',
        },

   
});

class Imagen extends Component {
    constructor(props){
        super(props);
    
        this.state = {
            imagen: [],
            ancho:'',
            alto:'',
        }   
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        
    }
    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
     };

    componentDidMount(){
        axios.get('http://localhost:8000/api/imagen/'+this.props.id).then(res=>{
            const personas = res.data;
            this.setState({personas});
        });
    }
    handleSubmit() { 
       
        const medidas = {
            ancho:this.state.ancho,
            alto:this.state.alto,
        };
    
        axios.post('http://localhost:8000/api/imagen/'+this.props.id, {medidas}).then(res => {
           
            const respuesta = res.data;
            this.setState({respuesta});
            this.setState({ toDashboard: true});

        });
        
        
    }
    

    render() {
        const {classes} = this.props;
        if (this.state.toDashboard === true) {
            return <Redirect to={{
                pathname: '/',
                state: { data: this.state.respuesta}
            }} />
          }
        return (
                    <form className={classes.container} noValidate autoComplete="off">
                        <GridList cellHeight={200} spacing={1} className={classes.gridList}>
                         <GridListTile key={this.state.imagen.id} cols={this.state.imagen.featured ? 2 : 1} rows={this.state.imagen.row ? 2 : 1}>
                            <img src={'/images/'+this.state.imagen.image} alt={this.state.imagen.name} />
                            <img src={'/images/'+this.state.imagen.image} alt={this.state.imagen.name} style={{ height:this.state.alto,width:this.state.ancho}}/>
                        </GridListTile>
           


         </GridList>
                        <TextField
                            required
                            id="standard-required"
                            label="Ancho"
                            className={classes.textField}
                            onChange={this.handleChange('ancho')}
                            margin="normal"
                        />
                        <TextField
                            required
                            id="standard-required"
                            label="Alto"
                            className={classes.textField}
                            onChange={this.handleChange('alto')}
                            margin="normal"
                        />
                        <Button variant="contained" color="primary" className={classes.button} onClick={this.handleSubmit}>
                            Primary
                        </Button>
                    </form>
        );
    }
}
Imagen.propTypes = {
    classes: PropTypes.object.isRequired,
};
   export default withStyles(styles)(Imagen);;
