import React, { Component } from 'react';
import  {withStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import AppBar from '@material-ui/core/AppBar';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
const styles = theme => ({

   
    root: {
        width: '100%',
        marginTop: theme.spacing.unit * 3,
        overflowX: 'auto',
    },
    button: {
        margin: theme.spacing.unit,
        height:35,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',

    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit,
        width: 200,
        marginTop: 0,
        marginBottom:0,
    },
    contenedor:
        {
            align: 'center',
        },

    heading: {
        fontSize: theme.typography.pxToRem(15),
        flexBasis: '33.33%',
        flexShrink: 0,
    },
    secondaryHeading: {
        fontSize: theme.typography.pxToRem(15),
        color: theme.palette.text.secondary,
    },
});
class Formulario extends Component {
    constructor(props){
        super(props);
    
        this.state = {
            habitaciones: [],
            expanded: 0,
            descripcion:'',
            estado:'',
            precio:'',
            numCamas:'',
            
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangePanel = this.handleChangePanel.bind(this);
        
    }
    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
     };
    handleSubmit() { 
       
        const data = {
            descripcion:this.state.descripcion,
            estado:this.state.estado,
            precio:this.state.precio,
            numCamas:this.state.numCamas,
            
        };
        console.log(data);
        axios.post('http://localhost:8080/hotel/', {data}).then(res => {
            console.log(res);
            console.log(res.data);
            axios.get('http://localhost:8080/hotel/habitaciones/').then(res=>{
            const habitaciones = res.data;
            this.setState({habitaciones});
            });
          });
        
        
    }
    handleChangePanel = panel => (event, expanded) => {
      
        this.setState({
          expanded: expanded ? panel : false,
        });
      };
    componentDidMount(){
        axios.get('http://localhost:8080/hotel/habitaciones/').then(res=>{
            const habitaciones = res.data;
            this.setState({habitaciones});
        });
    };
    render() {
        const { classes } = this.props;
        const { expanded } = this.state.expanded;
        return (            
            <form>
                <AppBar>INGRESE UNA NUEVA HABITACION</AppBar>
                <br/>
                <br/>
                <br/>
                <TextField
                    label="Descripcion"
                    className={classes.textField}
                    onChange={this.handleChange('descripcion')}
                />
                <TextField                           
                    label="Estado"
                    className={classes.textField}
                    onChange={this.handleChange('estado')}
                />
                <TextField
                    label="Precio"
                    type="number"
                    onChange={this.handleChange('precio')}
                    className={classes.textField}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    
                />
                <TextField
                     label="numCamas"
                     value={this.state.numCamas}
                     onChange={this.handleChange('numCamas')}
                     type="number"
                     className={classes.textField}
                     InputLabelProps={{
                       shrink: true,
                     }}
                     margin="normal"
                />
                <Button variant="contained" color="primary" className={classes.button} onClick={this.handleSubmit}>Guardar</Button>
                <br/>
                <br/>
                <br/>          
                <Paper>
                    {
                        this.state.habitaciones.map(habitacion => {
                        return (
                            <ExpansionPanel key={habitacion.idHabitacion} expanded={expanded == habitacion.idHabitacion} onChange={this.handleChangePanel(habitacion.idHabitacion)}>
                                <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
                                    <Typography className={classes.heading}>{habitacion.descripcion}</Typography>
                                    <Typography className={classes.secondaryHeading}>{habitacion.estado}</Typography>
                                </ExpansionPanelSummary>
                                <ExpansionPanelDetails>
                                    <Typography ><b>DURACIION: </b>{habitacion.precio}<br/><b>numCamas: </b>{habitacion.numCamas}</Typography>
                                </ExpansionPanelDetails>
                          </ExpansionPanel>
                          );
                        }
                    )
                }
            </Paper>
        </form>       

        );
    }
}
export default withStyles(styles)(Formulario);
