import React, { Component } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import ListSubheader from '@material-ui/core/ListSubheader';
import {  Redirect } from 'react-router-dom';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
      },
      gridList: {
        width: 650,
        height: 'auto',
        justifyContent: 'center',
        alignItems:"center"
      },
    button: {
        margin:theme.spacing(2),
        height:30,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
    },
    textField: {
        marginLeft:theme.spacing(2),
        marginRight:theme.spacing(2),
        width: 200,
        height: 'auto',
        marginTop: 0,
        marginBottom:0,
    },
    contenedor:
        {
            align: 'center',
        },

   
});

class Imagen extends Component {
    constructor(props){
        super(props);
        this.state = {
            imagen: [],
            ancho:0,
            alto:0,
            toDashboard: false,
            
        }   
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        
    }
    handleChange = name => event => {
        this.setState({ [name]: event.target.value });
        var img = document.getElementById('img2')
        if(name==='ancho'){
            img.width=event.target.value ;
        }else{
            img.height=event.target.value;
        }
     };
    componentDidMount(){
        
        if(this.props.location.state!=null){
            axios.get('http://localhost:8000/api/imagen/16').then(res=>{
                const imagen = res.data;
                this.setState({imagen});
                console.log(imagen)
            });
        }else{
            axios.get('http://localhost:8000/api/imagen/').then(res=>{
                const imagen = res.data;
                this.setState({imagen});
                this.setState({ toDashboard: true});              
            });
        }
    }
    handleSubmit() { 
        const medidas = {
            ancho:this.state.ancho,
            alto:this.state.alto,
        };
        
        axios.post('http://localhost:8000/api/imagen/'+this.props.location.state.data.id, {medidas}).then(res => {
            console.log(res);
            console.log(res.data);
            const respuesta = res.data;
            alert(respuesta);
            this.setState({respuesta});
            this.setState({ toDashboard: true});

        });
        
        
    }
    
    render() {
        const {classes} = this.props;
        if (this.state.toDashboard === true) {
            return <Redirect to={{
                pathname: '/galeria',
                state: { data: this.state.respuesta}
            }} />
          }
        return (
            <form className={classes.container} noValidate autoComplete="off">
                <AppBar position="static" color="default">
                        <Toolbar>
                            <Typography variant="h6" color="inherit">
                            Edicion de Imagen
                            </Typography>
                        </Toolbar>
                    </AppBar>
                <GridList  key={this.state.imagen.id} spacing={1} className={classes.gridList}>
                    <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                        <ListSubheader component="div">Imagen Original</ListSubheader>
                    </GridListTile>
                    <GridListTile  style={{ height: 'auto' ,width: 'auto' }}>
                        <img src={{uri: `data:image/jpg;base64,${this.state.imagen.image1}`}}alt={this.state.imagen.name} />
                    </GridListTile>
                    <GridListTile key="Subheader1" cols={2} style={{ height: 'auto' }}>
                        <ListSubheader component="div">Imagen Modificada</ListSubheader>
                    </GridListTile>
                    <GridListTile style={{ height: 'auto' ,width: 'auto' }}>
                        <img src={{uri: `data:image/jpg;base64,${this.state.imagen.image1}`}} alt={this.state.imagen.name}  id="img2"/>
                    </GridListTile>
                </GridList>
                <TextField
                    required
                    id="standard-required"
                    label="Ingrese el nuevo Alto de la imagen "
                    className={classes.textField}
                    onChange={this.handleChange('alto')}
                    margin="normal"
                />
                <TextField
                    required
                    id="standard-required"
                    label="Ingrese el nuevo Ancho de la imagen"
                    className={classes.textField}
                    onChange={this.handleChange('ancho')}
                    margin="normal"
                />
                <Button variant="contained" color="primary" className={classes.button} onClick={this.handleSubmit}>
                    Cambiar
                </Button>
            </form>
        );
    }
}
Imagen.propTypes = {
    classes: PropTypes.object.isRequired,
};
   export default withStyles(styles)(Imagen);;
