import React, { Component } from 'react';
import axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
    },
    gridList: {
      width: 500,
      height: 450,
    },
    gridList: {
      width: 'auto',
      height: 'auto',
    },
    icon: {
      color: 'rgba(255, 255, 255, 0.54)',
    },
  });
class Listado extends Component {

    state = {
        imagenes: [],
        
       

    }
    componentDidMount(){
        axios.get('http://172.16.0.64:8000/api/imagen').then(res=>{
            const imagenes = res.data;
            this.setState({imagenes});
        });
    }

    
    render() {
        const classes = styles();
        return (
            <div className={classes.root}>
            <GridList cellHeight={180} className={classes.gridList}>
              <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                <ListSubheader component="div">Galeria</ListSubheader>
              </GridListTile>
              {this.state.imagenes.map(imagen => (
                <GridListTile key={imagen.id} className={classes.gridListImg}>
                  <img src={'images/'+imagen.image} alt={imagen.name} />
                  <GridListTileBar
                    title={imagen.name}
                    actionIcon={
                      <IconButton aria-label={'info about' +imagen.name} className={classes.icon}>
                        <InfoIcon />
                      </IconButton>
                    }
                  />
                </GridListTile>
              ))}
            </GridList>
          </div>            
        );
    }
}

export default   withStyles(styles)(Listado);
