import React, { Component } from 'react';
import axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: 'translateZ(0)',
  },
  titleBar: {
    background:
      'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, ' +
      'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
  },
  icon: {
    color: 'white',
  },
  });
class Listado extends Component {

    state = {
        imagenes: [],
        
       

    }
    componentDidMount(){
        axios.get('http://172.16.0.64:8000/api/imagen').then(res=>{
            const imagenes = res.data;
            this.setState({imagenes});
        });
    }

    
    render() {
        const classes = styles();
        return (
         <div className={classes.root}>
          <GridList cellHeight={200} spacing={1} className={classes.gridList}>
           {imagenes.map(imagen => (
             <GridListTile key={imagen.id} cols={imagen.featured ? 2 : 1} rows={imagen.featured ? 2 : 1}>
               <img src={'/images/'+imagen.image} alt={imagen.name} />
               <GridListTileBar
                 title={imagen.name}
                 titlePosition="top"
                 actionIcon={
                   <IconButton aria-label={`star ${imagen.name}`} className={classes.icon}>
                     <StarBorderIcon />
                   </IconButton>
                 }
                 actionPosition="left"
                 className={classes.titleBar}
               />
             </GridListTile>
           ))}
         </GridList>
       </div>
        );
    }
}

export default   withStyles(styles)(Listado);
