import React,{Component} from 'react';
class Articulo extends Component{
    constructor(props){
        super(props);
        this.state={saludo:"Bienvenido este es el ",
                    saludo2:"Welcome this is the "};
    }
    render(props){
        if(this.props.idioma=="eng")
            return ( <div><h1>{this.state.saludo2}{this.props.titulo}</h1><article>{this.props.resumen}</article></div>)
        else
            return ( <div><h1>{this.state.saludo}{this.props.titulo}</h1><article>{this.props.resumen}</article></div>)
    }
}
export default Articulo;
