import React, { Component } from 'react';
import axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';


const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: 'white',
     
  },
  gridList: {
    width: 500,
    height: 450,
    direction:"row",
    justify:"center",
    alignItems:"center",
    
  
  },
  
  });
class Listado extends Component {

    state = {
        imagenes: [],
        
       

    }
    componentDidMount(){
        axios.get('http://localhost:8000/api/imagen').then(res=>{
            const imagenes = res.data;
            this.setState({imagenes});
        });
    }

    
    render() {
      const {classes} = this.props;
        return (
            <div className={classes.root}>
              <GridList cellHeight={250} className={classes.gridList} cols={2} style={{width:"80%",height:"80%", paddingLeft: "10%"}}>
                {this.state.imagenes.map(imagen => (
                  <GridListTile key={imagen.id} cols={imagen.col} rows={imagen.row} > 
                    <img src={'images/'+imagen.image} alt={imagen.name}  />       
                  </GridListTile>
                ))}
              </GridList>
            </div>            
        );
    }
}

export default   withStyles(styles)(Listado);
